import React from 'react';

const HeroSection: React.FC = () => {
  return (
    <section className="flex-[1.5] card overflow-hidden relative min-h-[300px]">
      <div className="relative h-full flex items-end p-8 bg-gradient-to-br from-[rgba(20,40,60,0.9)] via-[rgba(10,20,35,0.95)] to-[rgba(5,10,20,1)]">
        {/* Background glow effect */}
        <div className="absolute right-0 bottom-0 w-[60%] h-full">
          <div className="w-full h-full bg-gradient-to-t from-gold/20 via-gold/10 to-transparent" />
          <div className="absolute bottom-[20%] right-[30%] w-[150px] h-[150px] bg-gold/30 rounded-full blur-[40px]" />
        </div>
        
        {/* Text content */}
        <div className="relative z-10">
          <h1 className="text-[28px] font-bold leading-relaxed text-white drop-shadow-[2px_2px_10px_rgba(0,0,0,0.8)]">
            패배는 실수일 뿐입니다.
            <br />
            리플레이로 완벽해지세요.
          </h1>
          <div className="text-[40px] text-red-500 mt-5 opacity-80">♥</div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
