import React from 'react';

interface TableRoom {
  no: number;
  status: string;
  isPrivate: boolean;
  bb: number;
  minGold: string;
  title: string;
  players: string;
  canJoin: boolean;
}

const TableList: React.FC = () => {
  const tableData: TableRoom[] = [
    { no: 4, status: 'Ï∞∏Ïó¨Í∞ÄÎä•', isPrivate: false, bb: 100, minGold: '10000 G', title: 'Ï¶êÍ≤ÅÍ≤å Ïò¨Ïù∏', players: '2/7', canJoin: true },
    { no: 3, status: 'ÎπÑÎ∞ÄÎ∞©', isPrivate: true, bb: 200, minGold: '10000 G', title: 'Ïû¨ÎØ∏ÏûàÍ≤å Ï¶êÍ≤®Ïöî', players: '2/7', canJoin: true },
    { no: 2, status: 'Ï∞∏Ïó¨Í∞ÄÎä•', isPrivate: false, bb: 500, minGold: '10000 G', title: 'Ï¶êÍ≤ÅÍ≤å Ïò¨Ïù∏', players: '2/7', canJoin: true },
    { no: 1, status: 'ÎßåÏÑù', isPrivate: false, bb: 1000, minGold: '10000 G', title: 'Ïû¨ÎØ∏ÏûàÍ≤å Ï¶êÍ≤®Ïöî', players: '9/9', canJoin: true },
  ];

  const getStatusStyles = (status: string) => {
    switch (status) {
      case 'Ï∞∏Ïó¨Í∞ÄÎä•': return 'bg-green-400/15 text-green-400';
      case 'ÎπÑÎ∞ÄÎ∞©': return 'bg-gold/15 text-gold';
      case 'ÎßåÏÑù': return 'bg-gray-400/15 text-gray-400';
      default: return '';
    }
  };

  return (
    <section className="card p-6">
      <h2 className="text-lg font-bold text-white mb-5 tracking-wider">TABLE LIST</h2>
      
      <div className="overflow-x-auto">
        <table className="w-full min-w-[700px] border-collapse">
          <thead>
            <tr className="bg-[rgba(30,30,45,0.8)]">
              {['No', 'ÏÉÅÌÉú', 'BB', 'ÏµúÏÜå ÏûÖÏû• Í≥®Îìú', 'Î∞©Ï†úÎ™©', 'ÌòÑÏû¨Ïù∏Ïõê', '', ''].map((header, i) => (
                <th key={i} className="text-gray-400 text-[13px] font-semibold text-left px-3 py-4 border-b border-white/10">
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {tableData.map((room) => (
              <tr 
                key={room.no} 
                className={`hover:bg-gold/5 transition-colors ${room.status === 'ÎßåÏÑù' ? 'bg-[rgba(50,50,60,0.3)]' : ''}`}
              >
                <td className="px-3 py-4 text-sm text-white border-b border-white/5">{room.no}</td>
                <td className="px-3 py-4 text-sm border-b border-white/5">
                  <span className={`inline-flex items-center gap-1 px-3 py-1.5 rounded-full text-xs font-medium ${getStatusStyles(room.status)}`}>
                    {room.isPrivate && <span className="text-[10px]">üîí</span>}
                    {room.status}
                  </span>
                </td>
                <td className="px-3 py-4 text-sm text-white border-b border-white/5">{room.bb.toLocaleString()}</td>
                <td className="px-3 py-4 text-sm text-gold font-semibold border-b border-white/5">{room.minGold}</td>
                <td className="px-3 py-4 text-sm text-white border-b border-white/5">{room.title}</td>
                <td className="px-3 py-4 text-sm text-white border-b border-white/5">{room.players}</td>
                <td className="px-3 py-4 border-b border-white/5">
                  <button 
                    className={`px-5 py-2 rounded-full text-[13px] font-semibold transition-all ${
                      room.status === 'ÎßåÏÑù' 
                        ? 'bg-gray-400/30 text-gray-400 cursor-not-allowed' 
                        : 'bg-gold text-black hover:scale-105 hover:shadow-lg hover:shadow-gold/40'
                    }`}
                    disabled={room.status === 'ÎßåÏÑù'}
                  >
                    ÏûÖÏû•
                  </button>
                </td>
                <td className="px-3 py-4 border-b border-white/5">
                  <button className="btn-gold-outline px-5 py-2 rounded-full text-[13px]">Í¥ÄÏ†Ñ</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </section>
  );
};

export default TableList;
