import React, { useState } from 'react';
import './UserPanel.css';

const UserPanel: React.FC = () => {
  const [depositAmount, setDepositAmount] = useState('');
  const [withdrawAmount, setWithdrawAmount] = useState('');

  const userData = {
    name: 'ÌôçÍ∏∏Îèô',
    holdingGold: '3,000,000,000G',
    safeGold: '3,000,000,000G',
  };

  return (
    <div className="user-panel">
      <div className="panel-header">
        <h2 className="welcome-text">
          {userData.name} ÌöåÏõêÎãò ÏïàÎÖïÌïòÏÑ∏Ïöî.
        </h2>
        <button className="close-button">√ó</button>
      </div>

      <div className="gold-info">
        <div className="gold-row">
          <span className="gold-label">Î≥¥Ïú†Í≥®Îìú</span>
          <span className="gold-value">{userData.holdingGold}</span>
          <div className="gold-icon">
            <span className="coin">ü™ô</span>
          </div>
        </div>
        
        <div className="gold-row">
          <span className="gold-label">Î≥¥Í¥ÄÏ§ëÏù∏ Í≥®Îìú</span>
          <span className="gold-value">{userData.safeGold}</span>
        </div>
      </div>

      <div className="safe-actions">
        <div className="action-row">
          <span className="action-label">Î≥¥Í¥ÄÌïòÍ∏∞</span>
          <input
            type="text"
            className="action-input"
            placeholder="Ïà´ÏûêÎßå ÏûÖÎ†•"
            value={depositAmount}
            onChange={(e) => setDepositAmount(e.target.value)}
          />
          <button className="action-button deposit">Î≥¥Í¥ÄÌïòÍ∏∞</button>
        </div>

        <div className="action-row">
          <span className="action-label">Í∫ºÎÇ¥Í∏∞</span>
          <input
            type="text"
            className="action-input"
            placeholder="Ïà´ÏûêÎßå ÏûÖÎ†•"
            value={withdrawAmount}
            onChange={(e) => setWithdrawAmount(e.target.value)}
          />
          <button className="action-button withdraw">Í∫ºÎÇ¥Í∏∞</button>
        </div>
      </div>
    </div>
  );
};

export default UserPanel;
