import React from 'react';

const Header: React.FC = () => {
  const navItems = [
    { label: '트러스트홀덤 소개', href: '#' },
    { label: '자주하는 질문', href: '#' },
    { label: '고객센터', href: '#' },
    { label: '게임규칙', href: '#' },
  ];

  return (
    <header className="w-full">
      {/* Banner */}
      <div className="bg-gradient-to-r from-[#1a1a2e] via-[#2d2d44] to-[#1a1a2e] py-2.5 px-5 flex justify-center items-center gap-5 border-b border-white/10 flex-wrap">
        <span className="text-gray-400 text-sm">
          Trust the Game! 당신의 패배가 의심된다면, 리플레이를 켜세요.
        </span>
        <button className="btn-gold px-5 py-2 rounded-full text-sm">
          게임다시보기
        </button>
      </div>
      
      {/* Navigation */}
      <nav className="bg-[rgba(20,20,30,0.95)] py-4 px-5 border-b border-white/10">
        <div className="max-w-[1200px] mx-auto flex items-center justify-between">
          {/* Logo */}
          <div className="flex items-center">
            <div className="w-20 h-20 rounded-full bg-gradient-to-br from-[#1a3a5c] to-[#0d1f33] border-[3px] border-gold flex flex-col justify-center items-center shadow-[0_0_20px_rgba(212,168,67,0.3)]">
              <span className="text-gold text-base font-bold tracking-wider">TRUST</span>
              <span className="text-gold text-[8px] tracking-[2px]">HOLDEM</span>
            </div>
          </div>
          
          {/* Menu */}
          <ul className="hidden md:flex items-center gap-4">
            {navItems.map((item, index) => (
              <React.Fragment key={item.label}>
                <li>
                  <a href={item.href} className="text-white text-sm font-medium px-4 py-2 hover:text-gold transition-colors">
                    {item.label}
                  </a>
                </li>
                {index < navItems.length - 1 && (
                  <li className="text-gold text-[10px] opacity-60">♦</li>
                )}
              </React.Fragment>
            ))}
          </ul>
        </div>
      </nav>
    </header>
  );
};

export default Header;
