import React, { useState } from 'react';

const UserPanel: React.FC = () => {
  const [depositAmount, setDepositAmount] = useState('');
  const [withdrawAmount, setWithdrawAmount] = useState('');

  const userData = {
    name: 'ÌôçÍ∏∏Îèô',
    holdingGold: '3,000,000,000G',
    safeGold: '3,000,000,000G',
  };

  return (
    <div className="flex-1 card-light p-6 min-w-[350px]">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-base font-semibold text-white">
          {userData.name} ÌöåÏõêÎãò ÏïàÎÖïÌïòÏÑ∏Ïöî.
        </h2>
        <button className="bg-transparent text-gray-400 text-2xl w-8 h-8 flex items-center justify-center hover:text-white transition-colors">
          √ó
        </button>
      </div>

      {/* Gold Info */}
      <div className="mb-6 pb-5 border-b border-white/10">
        <div className="flex items-center mb-3">
          <span className="text-gray-400 text-sm min-w-[100px]">Î≥¥Ïú†Í≥®Îìú</span>
          <span className="text-gold text-xl font-bold flex-1">{userData.holdingGold}</span>
          <span className="text-3xl">ü™ô</span>
        </div>
        
        <div className="flex items-center">
          <span className="text-gray-400 text-sm min-w-[100px]">Î≥¥Í¥ÄÏ§ëÏù∏ Í≥®Îìú</span>
          <span className="text-gold text-xl font-bold flex-1">{userData.safeGold}</span>
        </div>
      </div>

      {/* Safe Actions */}
      <div className="flex flex-col gap-4">
        <div className="flex items-center gap-3">
          <span className="text-gray-400 text-sm min-w-[70px]">Î≥¥Í¥ÄÌïòÍ∏∞</span>
          <input
            type="text"
            className="flex-1 bg-white/10 border border-white/10 rounded-lg px-4 py-3 text-white text-sm placeholder:text-gray-500 focus:border-gold transition-colors"
            placeholder="Ïà´ÏûêÎßå ÏûÖÎ†•"
            value={depositAmount}
            onChange={(e) => setDepositAmount(e.target.value)}
          />
          <button className="btn-gold px-5 py-3 rounded-lg text-sm min-w-[90px]">Î≥¥Í¥ÄÌïòÍ∏∞</button>
        </div>

        <div className="flex items-center gap-3">
          <span className="text-gray-400 text-sm min-w-[70px]">Í∫ºÎÇ¥Í∏∞</span>
          <input
            type="text"
            className="flex-1 bg-white/10 border border-white/10 rounded-lg px-4 py-3 text-white text-sm placeholder:text-gray-500 focus:border-gold transition-colors"
            placeholder="Ïà´ÏûêÎßå ÏûÖÎ†•"
            value={withdrawAmount}
            onChange={(e) => setWithdrawAmount(e.target.value)}
          />
          <button className="btn-gold-outline px-5 py-3 rounded-lg text-sm min-w-[90px]">Í∫ºÎÇ¥Í∏∞</button>
        </div>
      </div>
    </div>
  );
};

export default UserPanel;
