import React from 'react';
import './TableList.css';

interface TableRoom {
  no: number;
  status: string;
  isPrivate: boolean;
  bb: number;
  minGold: string;
  title: string;
  players: string;
  canJoin: boolean;
}

const TableList: React.FC = () => {
  const tableData: TableRoom[] = [
    {
      no: 4,
      status: 'Ï∞∏Ïó¨Í∞ÄÎä•',
      isPrivate: false,
      bb: 100,
      minGold: '10000 G',
      title: 'Ï¶êÍ≤ÅÍ≤å Ïò¨Ïù∏',
      players: '2/7',
      canJoin: true,
    },
    {
      no: 3,
      status: 'ÎπÑÎ∞ÄÎ∞©',
      isPrivate: true,
      bb: 200,
      minGold: '10000 G',
      title: 'Ïû¨ÎØ∏ÏûàÍ≤å Ï¶êÍ≤®Ïöî',
      players: '2/7',
      canJoin: true,
    },
    {
      no: 2,
      status: 'Ï∞∏Ïó¨Í∞ÄÎä•',
      isPrivate: false,
      bb: 500,
      minGold: '10000 G',
      title: 'Ï¶êÍ≤ÅÍ≤å Ïò¨Ïù∏',
      players: '2/7',
      canJoin: true,
    },
    {
      no: 1,
      status: 'ÎßåÏÑù',
      isPrivate: false,
      bb: 1000,
      minGold: '10000 G',
      title: 'Ïû¨ÎØ∏ÏûàÍ≤å Ï¶êÍ≤®Ïöî',
      players: '9/9',
      canJoin: true,
    },
  ];

  const getStatusClass = (status: string) => {
    switch (status) {
      case 'Ï∞∏Ïó¨Í∞ÄÎä•':
        return 'status-available';
      case 'ÎπÑÎ∞ÄÎ∞©':
        return 'status-private';
      case 'ÎßåÏÑù':
        return 'status-full';
      default:
        return '';
    }
  };

  return (
    <section className="table-list">
      <h2 className="table-title">TABLE LIST</h2>
      
      <div className="table-container">
        <table className="game-table">
          <thead>
            <tr>
              <th>No</th>
              <th>ÏÉÅÌÉú</th>
              <th>BB</th>
              <th>ÏµúÏÜå ÏûÖÏû• Í≥®Îìú</th>
              <th>Î∞©Ï†úÎ™©</th>
              <th>ÌòÑÏû¨Ïù∏Ïõê</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {tableData.map((room) => (
              <tr key={room.no} className={room.status === 'ÎßåÏÑù' ? 'row-full' : ''}>
                <td>{room.no}</td>
                <td>
                  <span className={`status-badge ${getStatusClass(room.status)}`}>
                    {room.isPrivate && <span className="lock-icon">üîí</span>}
                    {room.status}
                  </span>
                </td>
                <td>{room.bb.toLocaleString()}</td>
                <td className="gold-cell">{room.minGold}</td>
                <td>{room.title}</td>
                <td>{room.players}</td>
                <td>
                  <button className={`join-button ${room.status === 'ÎßåÏÑù' ? 'disabled' : ''}`}>
                    ÏûÖÏû•
                  </button>
                </td>
                <td>
                  <button className="spectate-button">Í¥ÄÏ†Ñ</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </section>
  );
};

export default TableList;
